import { api } from "@/services.js";
export default {
  name: "ProdutoAdicionar",
  data: function data() {
    return {
      produto: {
        nome: "",
        preco: "",
        descricao: "",
        fotos: null,
        vendido: "false"
      }
    };
  },
  methods: {
    formatarProduto: function formatarProduto() {
      this.produto.usuario_id = this.$store.state.usuario.id;
    },
    adicionarProduto: function adicionarProduto() {
      var _this = this;

      this.formatarProduto();
      api.post("/produto", this.produto).then(function () {
        _this.$store.dispatch("getUsuarioProdutos");
      });
    }
  }
};
